import { alpha3CountryMapper , alpha2CountryMapper} from '../constants'

export const alpha3ToAlpha2 = (alpha3Code: string): string => {
    return (alpha3CountryMapper as any)[alpha3Code].countryCode
}

export const alpha3ToName = (alpha3Code: string): string => {
    return (alpha3CountryMapper as any)[alpha3Code].countryName
}

export const alpha2ToAlpha3 = (alpha2Code: string): string => {
    return (alpha2CountryMapper as any)[alpha2Code].countryCode
}

export const alpha2ToName = (alpha2Code: string): string => {
    return (alpha2CountryMapper as any)[alpha2Code].countryName
}