import { PaymentStatus, PaymentSubStatus, PaymentStatusSessions } from '../enums'

export const defineSessionPaymentsSubStatus = (paymentStatus: PaymentStatus) => {
  const paymentsStatusObject = {
    [PaymentStatus.PaidOut]: {
      paymentStatus: PaymentStatusSessions.Paid,
      paymentSubStatus: PaymentSubStatus.PaidAndClosed,
    },
    [PaymentStatus.StartPayment]: {
      paymentStatus: PaymentStatusSessions.Unpaid,
      paymentSubStatus: PaymentSubStatus.PaidAndWaitingForAdyenNotification,
    },
    [PaymentStatus.InPayment]: {
      paymentStatus: PaymentStatusSessions.Unpaid,
      paymentSubStatus: PaymentSubStatus.PaidAndWaitingForAdyenNotification,
    },
    [PaymentStatus.Canceled]: {
      paymentStatus: PaymentStatusSessions.Unpaid,
      paymentSubStatus: PaymentSubStatus.PaymentFailedForAnyReason,
    },
  }
  return Object.keys(paymentsStatusObject).includes(paymentStatus)
    ? paymentsStatusObject[paymentStatus as keyof typeof paymentsStatusObject]
    : {
        paymentStatus: PaymentStatusSessions.Unpaid,
        paymentSubStatus: PaymentSubStatus.PaymentFailedForAnyReason,
      }
}
