import { DeviceTypes, CreateWaySessionsType } from '../enums'

export const verifyIsDeviceRequest = (deviceType: string): { isDevice: boolean; deviceType: string | null } => {
  const aptResult = { isDevice: true, deviceType: DeviceTypes.APT }
  const qrCodeResult = { isDevice: true, deviceType: DeviceTypes.QR_CODE }

  const result = {
    [DeviceTypes.APT]: aptResult,
    [DeviceTypes.QR_CODE]: qrCodeResult,
    [CreateWaySessionsType.APT_START_SESSION]: aptResult,
    [CreateWaySessionsType.QR_CODE_START_SESSION]: qrCodeResult,
  }
  return result[deviceType as keyof typeof result] || { isDevice: false, deviceType: null }
}
