/**
 * This function receives an array and returns a new array without duplicated
 * elements.
 * Note that it will only work with types directly comparable with comparison
 * operator (===).
 * If you need to use a function for more complex types like object, you must
 * use a different function.
 */
export function filterDuplicates<T>(arr: T[]): T[] {
  return Array.from(new Set(arr))
}

export default filterDuplicates
