/**
 * Parses a value to a string if type of value is different to undefined or null
 * If type of value is undefined or null return null.
 * This way we avoid having 'undefined' or 'null' as result and we can further
 * validate the string post parsing attempt
 */
export function safeParseString(value: undefined | null): undefined
export function safeParseString(value: unknown): string
export function safeParseString(value: unknown): string | undefined {
  if (typeof value === 'undefined' || value === null) {
    return undefined
  }
  return String(value)
}

export default safeParseString
