import dbConnection from "evio-library-connections";
import { CollectionNames, DBNames } from "evio-library-commons";

let conn: any;

(async () => {
    conn = await dbConnection.connect(DBNames.Configs);
})();

interface SessionAnalysis {
    minSessions: number;
    acceptableRadius: number;
    minPercentage: number;
}

export const retrieveCoordinatesSessionsConfig = async (): Promise<SessionAnalysis> => {
    try {
        return await dbConnection.findOneDocument(conn.db, CollectionNames.Configs.CoordinatesSessionsConfigs, {});
    } catch (error) {
        throw error;
    }
}