import dbConnection from "evio-library-connections";
import { DBNames } from "evio-library-commons";

let conn: any;

(async () => {
  conn = await dbConnection.connect(DBNames.Configs);
})();

export const retrieveCongigsPreAuthorization = async (): Promise<any> => {
  try {
    return await dbConnection.findOneDocument(conn.db, 'configTempPreAuthorization', {}); 
  } catch (error) {
    throw error;
  }
};
