import { z } from 'zod'

export const CountrySchema = z.object({
  _id: z.string(),
  countryCode: z.string().regex(/^[A-Za-z]{2}$/, {
    message: '"countryCode" must be a valid 2-letter ISO code',
  }),
  country: z.string().regex(/^[A-Za-z]{3}$/, {
    message: '"country" must be a valid 3-letter ISO code',
  }),
  countryName: z.string(),
  numericCode: z.number().int().optional(),
})

export type Country = z.infer<typeof CountrySchema>
