import * as mongoose from 'mongoose';
import { IMongoDBConnectionManager } from '../interfaces/interfaces';

export abstract class MongoDBConnectionManagerDecorator
  implements IMongoDBConnectionManager
{
  protected decorated: IMongoDBConnectionManager;

  constructor(manager: IMongoDBConnectionManager) {
    this.decorated = manager;
  }

  async connect(): Promise<mongoose.Connection> {
    return await this.decorated.connect();
  }

  async close(): Promise<void> {
    return await this.decorated.close();
  }

  checkConnections(): void {
    this.decorated.checkConnections();
  }
}
