import ITranslationStrategy from '../interfaces/translation-strategy.interface';
import { IWeblateRequest } from '../../schema_interfaces/weblate.interface';

export default class TranslationContext {
  private strategy: ITranslationStrategy;

  constructor(strategy: ITranslationStrategy) {
    this.strategy = strategy;
  }
  public setStrategy(strategy: ITranslationStrategy) {
    this.strategy = strategy;
  }
  public async executeStrategy(
    redisKey: string,
    weblateRequest?: IWeblateRequest
  ): Promise<Object | null> {
    return await this.strategy.execute(redisKey, weblateRequest);
  }
}
