import RedisConnection from 'evio-redis-connection';


export async function languageClearCache(): Promise<void> {
    const context = 'languageClearCache'
    console.info(`[${context}] - Clearing Redis cache...`);
    try {
        const translationKeys: string[] = await RedisConnection.keys('translation*');
        Promise.all(translationKeys.map(async (key) => {
            console.info(`[${context}] - Deleting key: ${key}`);
            RedisConnection.delete(key);
        }));
    } catch (error) {
        console.error(`[${context}] - Error clearing Redis cache: ${error}`);
    }
}
