type HandlerCB = (msg: any) => Promise<any>;

export interface IDeadQueueConfig {
    deadLetterExchange?: string;
    deadQueue?: string;
}

export interface IConsumeParams extends IDeadQueueConfig {
    queue: string;
    handleIncomingMessage: HandlerCB;
    teamsWebhookUrl: string;
    maxRetries?: number;
    limitToProcess?: number;
}

export interface IAssertQueueConfig {
    durable: boolean;
    arguments?: {
        [key: string]: string
    }
}

export interface IAssertQueueParams extends IDeadQueueConfig {
    queueName: string;
    durable: boolean;
}

export interface IHandleErrorParams extends IDeadQueueConfig {
    error: Error;
    maxRetries: number;
    msg: any;
    queue: string;
}